import React, {Component} from 'react';
import classNames from 'classnames';
import {AppTopbar} from './AppTopbar';
import {AppFooter} from './AppFooter';
import {AppMenu} from './AppMenu';
import {AppProfile} from './AppProfile';
import { Button } from 'primereact/button';
import {Route} from 'react-router-dom';
import {Dashboard} from './components/Dashboard';
import { CustomerSearch } from './components/CustomerSearch';
import { BagTracker } from './components/BagTracker';
import { OrderSheet } from './components/OrderSheet';
import { AddCustomers } from './components/AddCustomers';
import {FormsDemo} from './components/FormsDemo';
import {SampleDemo} from './components/SampleDemo';
import {DataDemo} from './components/DataDemo';
import {PanelsDemo} from './components/PanelsDemo';
import {OverlaysDemo} from './components/OverlaysDemo';
import {MenusDemo} from './components/MenusDemo';
import {MessagesDemo} from './components/MessagesDemo';
import {ChartsDemo} from './components/ChartsDemo';
import {MiscDemo} from './components/MiscDemo';
import {EmptyPage} from './components/EmptyPage';
import {Documentation} from "./components/Documentation";
import 'primereact/resources/themes/nova-light/theme.css';
import 'primereact/resources/primereact.min.css';
import 'primeicons/primeicons.css';
import 'primeflex/primeflex.css';
/*import '@fullcalendar/core/main.css';
import '@fullcalendar/daygrid/main.css';
import '@fullcalendar/timegrid/main.css';*/
import './layout/layout.scss';
import './App.scss';
import './App.css';


import firebase from 'firebase/app';
import 'firebase/database';

import 'firebase/auth';

// For Firebase JS SDK v7.20.0 and later, measurementId is optional
const firebaseConfig = {
    apiKey: "AIzaSyCgoj3XqIh91DYeJiC4bh7EpylDOqZ43cU",
    authDomain: "ink-tank-bulk-orders.firebaseapp.com",
    databaseURL: "https://ink-tank-bulk-orders-default-rtdb.firebaseio.com",
    projectId: "ink-tank-bulk-orders",
    storageBucket: "ink-tank-bulk-orders.appspot.com",
    messagingSenderId: "231641991110",
    appId: "1:231641991110:web:ddd67fd2b417573e7514c7",
    measurementId: "G-7TDVQM7S2X"
  };
firebase.initializeApp(firebaseConfig);

export const provider = new firebase.auth.GoogleAuthProvider();
export const auth = firebase.auth();

class App extends Component {

    constructor() {
        super();
        this.state = {
            layoutMode: 'static',
            layoutColorMode: 'dark',
            staticMenuInactive: false,
            overlayMenuActive: false,
            mobileMenuActive: false,
            user: null
        };
        this.login = this.login.bind(this); // <-- add this line
        this.logout = this.logout.bind(this);

        this.onWrapperClick = this.onWrapperClick.bind(this);
        this.onToggleMenu = this.onToggleMenu.bind(this);
        this.onSidebarClick = this.onSidebarClick.bind(this);
        this.onMenuItemClick = this.onMenuItemClick.bind(this);
        this.createMenu();
    }

    login() {
        auth.signInWithPopup(provider)
            .then((result) => {
                const user = result.user;
                console.log(user.email)
                console.log(user.displayName)
                this.setState({
                    user
                })
                {window.location.reload()}
                {window.location = '/'}

            });
    }

    logout() {
        auth.signOut()

            .then(() => {
                this.setState({
                    user: null
                })
                {window.location = '/#/sign-out'}

            });
    }

    onWrapperClick(event) {
        if (!this.menuClick) {
            this.setState({
                overlayMenuActive: false,
                mobileMenuActive: false
            });
        }

        this.menuClick = false;
    }

    onToggleMenu(event) {
        this.menuClick = true;

        if (this.isDesktop()) {
            if (this.state.layoutMode === 'overlay') {
                this.setState({
                    overlayMenuActive: !this.state.overlayMenuActive
                });
            }
            else if (this.state.layoutMode === 'static') {
                this.setState({
                    staticMenuInactive: !this.state.staticMenuInactive
                });
            }
        }
        else {
            const mobileMenuActive = this.state.mobileMenuActive;
            this.setState({
                mobileMenuActive: !mobileMenuActive
            });
        }

        event.preventDefault();
    }

    onSidebarClick(event) {
        this.menuClick = true;
    }

    onMenuItemClick(event) {

        if(!event.item.items) {
            this.setState({
                overlayMenuActive: false,
                mobileMenuActive: false
            })
        }

    }

    createMenu() {
        this.menu = [
          {label: 'Dashboard', icon: 'pi pi-fw pi-home', command: () => {window.location = '#/'}},
            {
                label: 'Customers',
                icon: 'pi pi-fw pi-users',
                items: [
                    {
                        label: 'Search',
                        icon: 'pi pi-fw pi-search',
                        to: '/customersearch'
                    },
                    {
                        label: 'Add New',
                        icon: 'pi pi-fw pi-user-plus',
                        to: '/addcustomers'
                    }

                ],
            },
            { label: 'Bag Tracker', icon: 'pi pi-fw pi-check', to: '/bagtracker' },
            { label: 'History', icon: 'pi pi-fw pi-inbox', to: '/ordersheet' },

        ];
    }

    addClass(element, className) {
        if (element.classList)
            element.classList.add(className);
        else
            element.className += ' ' + className;
    }

    removeClass(element, className) {
        if (element.classList)
            element.classList.remove(className);
        else
            element.className = element.className.replace(new RegExp('(^|\\b)' + className.split(' ').join('|') + '(\\b|$)', 'gi'), ' ');
    }

    isDesktop() {
        return window.innerWidth > 1024;
    }

    componentDidUpdate() {
        if (this.state.mobileMenuActive)
            this.addClass(document.body, 'body-overflow-hidden');
        else
            this.removeClass(document.body, 'body-overflow-hidden');
    }

    componentDidMount() {
        auth.onAuthStateChanged((user) => {
            if (user) {
