                customerArray.push(childSnapshot.toJSON());
            });
        });
        this.setState({ customers: customerArray });
        this.setState({ bulk_orders: customerArray });
        const orderArray = [];
        firebase.database().ref('/orders').on('value', function (snapshot) {
